mapper.select:
WITH data_flow_created_by AS ( SELECT s.id, s.category_id, s.name, s.avatar, s.description, s.created_by, s.created_date, u.fullname AS 'created_fullname', u.avatar AS 'created_avatar' FROM subject s INNER JOIN users u ON s.created_by = u.id WHERE s.delete_flag = 0 AND u.delete_flag = 0 ), data_flow_last_modifed_by AS ( SELECT s.id, s.category_id, s.name, s.avatar, s.description, s.last_modified_by, s.created_date, u.fullname AS 'last_modified_fullname', u.avatar AS 'last_modified_avatar' FROM subject s INNER JOIN users u ON s.last_modified_by = u.id WHERE s.delete_flag = 0 AND u.delete_flag = 0 ) SELECT data_1.id, data_1.category_id, data_1.name, data_1.avatar, data_1.description, data_1.created_by, data_1.created_fullname, data_1.created_avatar, data_1.created_date, data_2.last_modified_by, data_2.last_modified_fullname, data_2.last_modified_avatar FROM data_flow_created_by data_1 INNER JOIN data_flow_last_modifed_by data_2 ON data_1.id = data_2.id
WHERE (data_1.name LIKE CONCAT('%', #{keyword}, '%') OR data_1.description LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY ${meta.sortBy} ${meta.sortType} LIMIT #{meta.limit} OFFSET #{meta.offSet}

mapper.delete:
UPDATE subject
SET delete_flag = 1
WHERE id = #{id};
